import React, { useState } from "react";

// A base message screen layout with a conversation list on the left
// and a chat view on the right where you can type and send messages.
// TailwindCSS required.

const mockConversations = [
  {
    id: "1",
    name: "Alice Johnson",
    lastMessage: "Hey there! How’s your day going?",
    avatar: "https://images.unsplash.com/photo-1502685104226-ee32379fefbe?w=200&q=80&auto=format&fit=crop",
    messages: [
      { id: "m1", fromMe: false, text: "Hey there! How’s your day going?" },
      { id: "m2", fromMe: true, text: "Pretty good! Working on a new project." },
    ],
  },
  {
    id: "2",
    name: "Bob Smith",
    lastMessage: "Let’s catch up soon!",
    avatar: "https://images.unsplash.com/photo-1506794778202-cad84cf45f1d?w=200&q=80&auto=format&fit=crop",
    messages: [
      { id: "m1", fromMe: false, text: "Let’s catch up soon!" },
      { id: "m2", fromMe: true, text: "Definitely! How about Friday?" },
    ],
  },
];

export default function MessageScreen() {
  const [conversations, setConversations] = useState(mockConversations);
  const [selectedId, setSelectedId] = useState(conversations[0].id);
  const [input, setInput] = useState("");

  const selected = conversations.find((c) => c.id === selectedId);

  function handleSend(e) {
    e.preventDefault();
    const trimmed = input.trim();
    if (!trimmed) return;
    const newMsg = { id: Date.now().toString(), fromMe: true, text: trimmed };

    setConversations((prev) =>
      prev.map((c) =>
        c.id === selectedId
          ? { ...c, messages: [...c.messages, newMsg], lastMessage: trimmed }
          : c
      )
    );
    setInput("");
  }

  return (
    <div className="h-screen flex bg-gray-50">
      {/* Sidebar */}
      <aside className="w-1/3 max-w-sm border-r bg-white flex flex-col">
        <div className="p-4 border-b font-semibold text-lg">Messages</div>
        <div className="flex-1 overflow-auto">
          {conversations.map((c) => (
            <button
              key={c.id}
              onClick={() => setSelectedId(c.id)}
              className={`w-full flex items-center gap-3 p-3 hover:bg-gray-100 text-left ${
                selectedId === c.id ? "bg-gray-100" : ""
              }`}
            >
              <img
                src={c.avatar}
                alt={c.name}
                className="w-10 h-10 rounded-full object-cover"
              />
              <div className="flex-1">
                <div className="font-medium">{c.name}</div>
                <div className="text-sm text-gray-500 truncate">{c.lastMessage}</div>
              </div>
            </button>
          ))}
        </div>
      </aside>

      {/* Main Chat Area */}
      <main className="flex-1 flex flex-col">
        {selected ? (
          <>
            <div className="p-4 border-b flex items-center gap-3 bg-white">
              <img
                src={selected.avatar}
                alt={selected.name}
                className="w-10 h-10 rounded-full object-cover"
              />
              <div>
                <div className="font-semibold">{selected.name}</div>
                <div className="text-sm text-gray-500">Active now</div>
              </div>
            </div>

            <div className="flex-1 overflow-auto p-4 space-y-3">
              {selected.messages.map((m) => (
                <div
                  key={m.id}
                  className={`flex ${m.fromMe ? "justify-end" : "justify-start"}`}
                >
                  <div
                    className={`px-3 py-2 rounded-2xl max-w-[70%] break-words ${
                      m.fromMe
                        ? "bg-blue-600 text-white rounded-br-none"
                        : "bg-white border rounded-bl-none"
                    }`}
                  >
                    {m.text}
                  </div>
                </div>
              ))}
            </div>

            <form onSubmit={handleSend} className="p-4 border-t bg-white flex gap-2">
              <input
                type="text"
                value={input}
                onChange={(e) => setInput(e.target.value)}
                placeholder="Type a message..."
                className="flex-1 border rounded-lg px-3 py-2"
              />
              <button
                type="submit"
                className="bg-blue-600 text-white px-4 py-2 rounded-lg"
              >
                Send
              </button>
            </form>
          </>
        ) : (
          <div className="flex-1 flex items-center justify-center text-gray-500">
            Select a conversation to start chatting.
          </div>
        )}
      </main>
    </div>
  );
}
